import React, { useState, useEffect } from 'react';

const STRATEGY_LABELS = {
  cross: "Cross-Exchange",
  inter: "Inter-Asset",
  triangular: "Triangular",
  stat: "Statistical",
  funding: "Funding"
};
ok 
const STRATEGY_KEYS = Object.keys(STRATEGY_LABELS);

function Scanner() {
  const [allOpps, setAllOpps] = useState({});
  const [strategy, setStrategy] = useState("cross");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch('http://localhost:3000/api/opportunities');
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        const data = await response.json();
        setAllOpps(data);
        setLastUpdated(new Date());
      } catch (err) {
        setError("Failed to fetch data. Please ensure the backend server is running and accessible.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
    const intervalId = setInterval(fetchData, 15000);
    return () => clearInterval(intervalId);
  }, []);

  const opps = allOpps[strategy] || [];

  return (
    <div className="mb-4 bg-gray-800 p-6 rounded-xl shadow-lg border border-gray-700">
      <h2 className="text-3xl font-extrabold text-blue-300 mb-4 tracking-wide text-center">Opportunities</h2>
      {/* Strategy Tabs */}
      <div className="flex justify-center mb-6">
        {STRATEGY_KEYS.map(key => (
          <button
            key={key}
            onClick={() => setStrategy(key)}
            className={`px-4 py-2 mx-1 rounded-t-lg font-semibold transition-all duration-200
              ${strategy === key
                ? "bg-blue-500 text-white shadow"
                : "bg-gray-700 text-blue-200 hover:bg-blue-600 hover:text-white"}
            `}
          >
            {STRATEGY_LABELS[key]}
          </button>
        ))}
      </div>
      {/* Opportunity Count & Last Updated */}
      <div className="flex justify-between items-center mb-2 text-sm text-gray-400">
        <span>
          {opps.length > 0
            ? <span className="text-green-400 font-bold">{opps.length} opportunity{opps.length > 1 ? "ies" : "y"} found</span>
            : "No opportunities found"}
        </span>
        {lastUpdated && (
          <span>Last updated: {lastUpdated.toLocaleTimeString()}</span>
        )}
      </div>
      {/* Table */}
      <div className="overflow-x-auto rounded-lg">
        <table className="min-w-full bg-gray-900 border border-gray-700 rounded-lg">
          <thead className="bg-gray-700 sticky top-0 z-10">
            <tr>
              {strategy === "cross" && (
                <>
                  <th className="px-4 py-3">Pair</th>
                  <th className="px-4 py-3">Buy From</th>
                  <th className="px-4 py-3">Buy Price</th>
                  <th className="px-4 py-3">Sell To</th>
                  <th className="px-4 py-3">Sell Price</th>
                  <th className="px-4 py-3">Profit %</th>
                </>
              )}
              {strategy === "inter" && (
                <>
                  <th className="px-4 py-3">Exchange</th>
                  <th className="px-4 py-3">Profit %</th>
                  <th className="px-4 py-3">ETH/USDT</th>
                  <th className="px-4 py-3">Implied</th>
                </>
              )}
              {/* Add more columns for other strategies */}
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-800">
            {opps.map((op, i) => (
              <tr
                key={i}
                className={parseFloat(op.profitPercent || op.profitPercentage) > 0.2
                  ? "bg-green-900/60 font-bold animate-pulse"
                  : "hover:bg-gray-800 transition"}
              >
                {strategy === "cross" && (
                  <>
                    <td>{op.pair}</td>
                    <td>{op.buyEx || op.buyExchange}</td>
                    <td>${parseFloat(op.buyPrice).toLocaleString()}</td>
                    <td>{op.sellEx || op.sellExchange}</td>
                    <td>${parseFloat(op.sellPrice).toLocaleString()}</td>
                    <td className="text-green-400">{op.profitPercent || op.profitPercentage}%</td>
                  </>
                )}
                {strategy === "inter" && (
                  <>
                    <td>{op.exchange}</td>
                    <td className="text-green-400">{op.profitPercent}%</td>
                    <td>{op.ethUsdt}</td>
                    <td>{op.implied}</td>
                  </>
                )}
                {/* Add more row rendering for other strategies */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {loading && <p className="text-blue-300 text-center text-lg mt-4 animate-pulse">Loading opportunities...</p>}
      {error && <p className="text-red-500 text-center text-lg mt-4">{error}</p>}
    </div>
  );
}

export default Scanner;
