import React, { useState, useEffect } from 'react';
import { ArrowTrendingUp, Flame } from 'lucide-react';

const STRATEGY_LABELS = {
  cross: "Cross-Exchange",
  inter: "Inter-Asset",
  triangular: "Triangular",
  stat: "Statistical",
  funding: "Funding"
};
const STRATEGY_KEYS = Object.keys(STRATEGY_LABELS);

function Scanner() {
  const [allOpps, setAllOpps] = useState({});
  const [lastNonEmptyOpps, setLastNonEmptyOpps] = useState({});
  const [strategy, setStrategy] = useState("cross");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [lastSeen, setLastSeen] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch('http://localhost:3000/api/opportunities');
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        const data = await response.json();
        setAllOpps(data);
        setLastUpdated(new Date());

        STRATEGY_KEYS.forEach(key => {
          if (Array.isArray(data[key]) && data[key].length > 0) {
            setLastNonEmptyOpps(prev => ({ ...prev, [key]: data[key] }));
            setLastSeen(prev => ({ ...prev, [key]: new Date() }));
          }
        });
      } catch (err) {
        setError("Failed to fetch data. Please ensure the backend server is running and accessible.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
    const intervalId = setInterval(fetchData, 15000);
    return () => clearInterval(intervalId);
  }, []);

  const opps = allOpps[strategy]?.length > 0
    ? allOpps[strategy]
    : lastNonEmptyOpps[strategy] || [];

  return (
    <div className="mb-4 bg-[#1a1c1e] p-6 rounded-2xl shadow-2xl border border-yellow-700 animate-fade-in">
      <h2 className="text-4xl font-extrabold text-yellow-400 mb-6 tracking-wide text-center uppercase flex items-center justify-center gap-2">
        <Flame className="w-7 h-7 text-yellow-500 animate-bounce" /> Opportunities
      </h2>

      <div className="flex justify-center mb-6">
        {STRATEGY_KEYS.map(key => (
          <button
            key={key}
            onClick={() => setStrategy(key)}
            className={`px-4 py-2 mx-1 rounded-xl font-semibold shadow-md transition-all duration-300 text-sm
              ${strategy === key
                ? "bg-yellow-500 text-black scale-105"
                : "bg-gray-800 text-yellow-300 hover:bg-yellow-600 hover:text-black"}
            `}
          >
            {STRATEGY_LABELS[key]}
          </button>
        ))}
      </div>

      <div className="flex justify-between items-center mb-4 text-sm text-gray-400">
        <span>
          {opps.length > 0
            ? <span className="text-green-400 font-bold">{opps.length} opportunity{opps.length > 1 ? "ies" : "y"} found</span>
            : lastNonEmptyOpps[strategy]
              ? <span className="text-yellow-400 font-bold">No current opportunities. Showing last found.</span>
              : "No opportunities found"}
        </span>
        <span>
          Last updated: {lastUpdated && lastUpdated.toLocaleTimeString()}
          {lastSeen[strategy] && (
            <span className="ml-2 text-yellow-500">| Last seen: {lastSeen[strategy].toLocaleTimeString()}</span>
          )}
        </span>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {opps.map((op, i) => (
          <div
            key={i}
            className={`p-4 rounded-xl border shadow-lg transition-transform duration-300 hover:scale-[1.02]
              ${parseFloat(op.profitPercent || op.profitPercentage) > 0.3
                ? "bg-gradient-to-r from-yellow-600 to-yellow-400 border-yellow-300 text-black animate-glow"
                : "bg-gray-800 border-gray-700 text-white"}
            `}
          >
            <div className="flex justify-between items-center mb-2">
              <div className="text-lg font-bold tracking-wide text-yellow-300">{op.pair || op.exchange}</div>
              <div className="text-sm text-green-400 font-semibold">{(op.profitPercent || op.profitPercentage)}%</div>
            </div>
            <div className="text-sm space-y-1">
              {strategy === "cross" && (
                <>
                  <div><span className="text-yellow-400 font-medium">Buy from:</span> {op.buyEx || op.buyExchange} @ <span className="text-green-300">${parseFloat(op.buyPrice).toLocaleString()}</span></div>
                  <div><span className="text-yellow-400 font-medium">Sell to:</span> {op.sellEx || op.sellExchange} @ <span className="text-red-300">${parseFloat(op.sellPrice).toLocaleString()}</span></div>
                </>
              )}
              {strategy === "inter" && (
                <>
                  <div><span className="text-yellow-400 font-medium">ETH/USDT:</span> {op.ethUsdt}</div>
                  <div><span className="text-yellow-400 font-medium">Implied:</span> {op.implied}</div>
                </>
              )}
            </div>
            <div className="flex justify-end mt-2">
              <ArrowTrendingUp className="w-5 h-5 text-green-400 animate-bounce" />
            </div>
          </div>
        ))}
      </div>

      {loading && <p className="text-yellow-300 text-center text-lg mt-6 animate-pulse">‚è≥ Loading opportunities...</p>}
      {error && <p className="text-red-500 text-center text-lg mt-6">{error}</p>}
    </div>
  );
}

export default Scanner;
