import React, { useState, useEffect } from 'react';

const STRATEGY_LABELS = {
  cross: "Cross-Exchange",
  inter: "Inter-Asset",
  triangular: "Triangular",
  stat: "Statistical",
  funding: "Funding"
};
const STRATEGY_KEYS = Object.keys(STRATEGY_LABELS);

function Scanner() {
  const [allOpps, setAllOpps] = useState({});
  const [lastNonEmptyOpps, setLastNonEmptyOpps] = useState({});
  const [strategy, setStrategy] = useState("cross");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [lastSeen, setLastSeen] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch('http://localhost:3000/api/opportunities');
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        const data = await response.json();
        setAllOpps(data);
        setLastUpdated(new Date());

        STRATEGY_KEYS.forEach(key => {
          if (Array.isArray(data[key]) && data[key].length > 0) {
            setLastNonEmptyOpps(prev => ({ ...prev, [key]: data[key] }));
            setLastSeen(prev => ({ ...prev, [key]: new Date() }));
          }
        });
      } catch (err) {
        setError("Failed to fetch data. Please ensure the backend server is running and accessible.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
    const intervalId = setInterval(fetchData, 15000);
    return () => clearInterval(intervalId);
  }, []);

  const opps = allOpps[strategy]?.length > 0
    ? allOpps[strategy]
    : lastNonEmptyOpps[strategy] || [];

  return (
    <div className="mb-6 bg-zinc-900 p-6 rounded-2xl shadow-2xl border border-zinc-700 animate-fade-in">
      <h2 className="text-4xl font-bold text-sky-300 mb-6 tracking-wide text-center animate-fade-in-down">
        üöÄ Arbitrage Opportunities
      </h2>

      <div className="flex justify-center mb-4 gap-2 flex-wrap">
        {STRATEGY_KEYS.map(key => (
          <button
            key={key}
            onClick={() => setStrategy(key)}
            className={`px-4 py-2 rounded-xl font-semibold transition-all duration-300 border 
              ${strategy === key
                ? "bg-sky-600 text-white border-sky-500 shadow-lg"
                : "bg-zinc-800 text-sky-300 hover:bg-sky-700 hover:text-white border-zinc-600"}
            `}
          >
            {STRATEGY_LABELS[key]}
          </button>
        ))}
      </div>

      <div className="flex justify-between items-center mb-3 text-sm text-zinc-400 px-2">
        <span>
          {opps.length > 0 ? (
            <span className="text-emerald-400 font-bold animate-pulse">
              {opps.length} opportunity{opps.length > 1 ? "ies" : "y"} found
            </span>
          ) : lastNonEmptyOpps[strategy] ? (
            <span className="text-yellow-400 font-semibold">No live opportunities. Showing last seen.</span>
          ) : "No data available."}
        </span>
        <span>
          ‚è± {lastUpdated && lastUpdated.toLocaleTimeString()}
          {lastSeen[strategy] && (
            <span className="ml-2 text-sky-400">
              | Last seen: {lastSeen[strategy].toLocaleTimeString()}
            </span>
          )}
        </span>
      </div>

      <div className="overflow-x-auto rounded-xl">
        <table className="min-w-full bg-zinc-950 border border-zinc-700 rounded-xl">
          <thead className="bg-zinc-800 text-sky-300 text-sm">
            <tr>
              {strategy === "cross" && (
                <>
                  <th className="px-4 py-2">Pair</th>
                  <th className="px-4 py-2">Buy From</th>
                  <th className="px-4 py-2">Buy Price</th>
                  <th className="px-4 py-2">Sell To</th>
                  <th className="px-4 py-2">Sell Price</th>
                  <th className="px-4 py-2">Profit %</th>
                </>
              )}
            </tr>
          </thead>
          <tbody className="divide-y divide-zinc-800 text-sm">
            {opps.map((op, i) => (
              <tr
                key={i}
                className={`transition-all duration-300 ${parseFloat(op.profitPercent || op.profitPercentage) > 0.2
                  ? "bg-emerald-900/50 text-emerald-300 font-bold animate-flash"
                  : "hover:bg-zinc-800"}`}
              >
                {strategy === "cross" && (
                  <>
                    <td className="px-4 py-3 text-white">{op.pair}</td>
                    <td className="px-4 py-3 text-sky-300">{op.buyEx || op.buyExchange}</td>
                    <td className="px-4 py-3 text-rose-400">${parseFloat(op.buyPrice).toLocaleString()}</td>
                    <td className="px-4 py-3 text-sky-300">{op.sellEx || op.sellExchange}</td>
                    <td className="px-4 py-3 text-green-400">${parseFloat(op.sellPrice).toLocaleString()}</td>
                    <td className="px-4 py-3 text-emerald-400 font-semibold">{op.profitPercent || op.profitPercentage}%</td>
                  </>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {loading && <p className="text-sky-400 text-center text-base mt-6 animate-pulse">Loading data...</p>}
      {error && <p className="text-red-500 text-center mt-6 font-semibold">{error}</p>}
    </div>
  );
}

export default Scanner;
