import React, { useState, useEffect } from 'react';

function Home() {
    const [arbitrageOpportunities, setArbitrageOpportunities] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchArbitrageData = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await fetch('http://localhost:3000/api/arbitrage?pairs=BTC-USDT,ETH-USDT');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setArbitrageOpportunities(data);
        } catch (e) {
            setError(e.message);
            setArbitrageOpportunities([]);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchArbitrageData(); // Fetch data on initial load

        const intervalId = setInterval(fetchArbitrageData, 5000); // Fetch data every 5 seconds

        return () => clearInterval(intervalId); // Cleanup interval on unmount
    }, []);

    if (loading) {
        return <div>Loading arbitrage opportunities...</div>;
    }

    if (error) {
        return <div>Error loading arbitrage opportunities: {error}</div>;
    }

    return (
        <div className="p-4">
            <h2 className="text-xl mb-4">Arbitrage Opportunities</h2>
            {arbitrageOpportunities.length > 0 ? (
                <table className="w-full border-collapse border border-gray-300">
                    <thead>
                        <tr className="bg-gray-100">
                            <th className="border border-gray-300 p-2">Pair</th>
                            <th className="border border-gray-300 p-2">Buy Exchange</th>
                            <th className="border border-gray-300 p-2">Buy Price</th>
                            <th className="border border-gray-300 p-2">Sell Exchange</th>
                            <th className="border border-gray-300 p-2">Sell Price</th>
                            <th className="border border-gray-300 p-2">Profit (%)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {arbitrageOpportunities.map((opportunity, index) => (
                            <tr key={index}>
                                <td className="border border-gray-300 p-2">{opportunity.pair}</td>
                                <td className="border border-gray-300 p-2">{opportunity.buyExchange}</td>
                                <td className="border border-gray-300 p-2">{opportunity.buyPrice}</td>
                                <td className="border border-gray-300 p-2">{opportunity.sellExchange}</td>
                                <td className="border border-gray-300 p-2">{opportunity.sellPrice}</td>
                                <td className="border border-gray-300 p-2">{opportunity.profitPercentage}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No arbitrage opportunities found.</p>
            )}
        </div>
    );
}

export default Home;
