import React, { useState, useEffect } from "react";

const STRATEGY_LABELS = {
  cross: "Cross-Exchange",
  inter: "Inter-Asset",
  triangular: "Triangular",
  stat: "Statistical",
  funding: "Funding"
};

const STRATEGY_KEYS = Object.keys(STRATEGY_LABELS);

export default function Scanner() {
  const [allOpps, setAllOpps] = useState({});
  const [lastNonEmptyOpps, setLastNonEmptyOpps] = useState({});
  const [strategy, setStrategy] = useState("cross");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [lastSeen, setLastSeen] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const res = await fetch("http://localhost:3000/api/opportunities");
        if (!res.ok) throw new Error("Failed to fetch data");
        const data = await res.json();
        setAllOpps(data);
        setLastUpdated(new Date());
        STRATEGY_KEYS.forEach((key) => {
          if (Array.isArray(data[key]) && data[key].length > 0) {
            setLastNonEmptyOpps((prev) => ({ ...prev, [key]: data[key] }));
            setLastSeen((prev) => ({ ...prev, [key]: new Date() }));
          }
        });
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
    const interval = setInterval(fetchData, 10000);
    return () => clearInterval(interval);
  }, []);

  const opps = allOpps[strategy]?.length ? allOpps[strategy] : lastNonEmptyOpps[strategy] || [];

  return (
    <div className="bg-gradient-to-br from-zinc-900 via-zinc-800 to-zinc-900 rounded-2xl shadow-2xl p-6 border border-zinc-700">
      <div className="text-center mb-6">
        <h2 className="text-3xl text-blue-400 font-extrabold tracking-wide animate-fade-in">ðŸ“Š Arbitrage Opportunities</h2>
        <p className="text-sm text-zinc-400">Live scanner for trading strategies</p>
      </div>

      <div className="flex justify-center gap-2 mb-4">
        {STRATEGY_KEYS.map((key) => (
          <button
            key={key}
            onClick={() => setStrategy(key)}
            className={`px-4 py-2 rounded-xl text-sm font-medium border transition-all duration-200
              ${strategy === key
                ? "bg-blue-500 text-white border-blue-400 shadow-lg"
                : "bg-zinc-800 text-blue-200 border-zinc-700 hover:bg-blue-600 hover:text-white"}`}
          >
            {STRATEGY_LABELS[key]}
          </button>
        ))}
      </div>

      <div className="flex justify-between items-center text-sm text-zinc-400 mb-3">
        <div>
          {opps.length > 0 ? (
            <span className="text-green-400 font-semibold">{opps.length} opportunity{opps.length > 1 ? "ies" : "y"} found</span>
          ) : lastNonEmptyOpps[strategy] ? (
            <span className="text-yellow-400">No new data. Showing last known results.</span>
          ) : (
            <span>No opportunities found yet.</span>
          )}
        </div>
        <div>
          {lastUpdated && <span>Updated: {lastUpdated.toLocaleTimeString()}</span>}
          {lastSeen[strategy] && (
            <span className="ml-2 text-blue-300">| Last seen: {lastSeen[strategy].toLocaleTimeString()}</span>
          )}
        </div>
      </div>

      <div className="overflow-x-auto">
        <table className="min-w-full text-sm text-left rounded-xl overflow-hidden">
          <thead className="bg-zinc-700 text-zinc-100">
            <tr>
              {strategy === "cross" && (
                <>
                  <th className="px-4 py-2">Pair</th>
                  <th className="px-4 py-2">Buy From</th>
                  <th className="px-4 py-2">Buy Price</th>
                  <th className="px-4 py-2">Sell To</th>
                  <th className="px-4 py-2">Sell Price</th>
                  <th className="px-4 py-2">Profit %</th>
                </>
              )}
            </tr>
          </thead>
          <tbody className="divide-y divide-zinc-800">
            {opps.map((op, idx) => (
              <tr
                key={idx}
                className={`transition duration-200 ease-in-out ${parseFloat(op.profitPercent) > 0.2
                  ? "bg-green-900/60 animate-pulse font-semibold"
                  : "hover:bg-zinc-800"}`}
              >
                {strategy === "cross" && (
                  <>
                    <td className="px-4 py-3 text-blue-300 font-mono">{op.pair}</td>
                    <td className="px-4 py-3">{op.buyEx || op.buyExchange}</td>
                    <td className="px-4 py-3 text-red-400">${parseFloat(op.buyPrice).toFixed(2)}</td>
                    <td className="px-4 py-3">{op.sellEx || op.sellExchange}</td>
                    <td className="px-4 py-3 text-green-400">${parseFloat(op.sellPrice).toFixed(2)}</td>
                    <td className="px-4 py-3 text-green-300 font-bold">{op.profitPercent}%</td>
                  </>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {loading && (
        <div className="mt-4 text-center text-blue-300 animate-pulse">Loading...</div>
      )}
      {error && (
        <div className="mt-4 text-center text-red-500">{error}</div>
      )}
    </div>
  );
}
