import React, { useEffect, useState } from 'react';

function Dashboard({ user }) {
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState({ username: '', password: '', role: 'user' });

  useEffect(() => {
    fetch('/api/users')
      .then(res => res.json())
      .then(setUsers);
  }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    await fetch('/api/users', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    });
    setForm({ username: '', password: '', role: 'user' });
    const refreshed = await fetch('/api/users').then(res => res.json());
    setUsers(refreshed);
  };

  if (!user || user.role !== "admin") {
    return (
      <div className="text-red-500 text-center text-lg mt-8 font-semibold">
        Access Denied: Admins Only
      </div>
    );
  }

  return (
    <div className="bg-gray-900 p-6 rounded-xl shadow-xl border border-yellow-500 text-yellow-100 max-w-4xl mx-auto mt-8">
      <h2 className="text-3xl font-extrabold text-yellow-400 mb-6 text-center tracking-wide">
        üõ†Ô∏è Admin Dashboard
      </h2>

      <form onSubmit={handleSubmit} className="flex flex-wrap items-center gap-4 mb-6 justify-center">
        <input
          name="username"
          value={form.username}
          onChange={handleChange}
          placeholder="Username"
          required
          className="px-4 py-2 rounded-lg bg-gray-800 text-white border border-yellow-500 focus:outline-none focus:ring-2 focus:ring-yellow-300"
        />
        <input
          name="password"
          value={form.password}
          onChange={handleChange}
          placeholder="Password"
          type="password"
          required
          className="px-4 py-2 rounded-lg bg-gray-800 text-white border border-yellow-500 focus:outline-none focus:ring-2 focus:ring-yellow-300"
        />
        <select
          name="role"
          value={form.role}
          onChange={handleChange}
          className="px-4 py-2 rounded-lg bg-gray-800 text-white border border-yellow-500 focus:outline-none"
        >
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>
        <button
          type="submit"
          className="px-6 py-2 bg-yellow-500 hover:bg-yellow-400 text-black font-bold rounded-lg transition"
        >
          ‚ûï Add User
        </button>
      </form>

      <div className="overflow-x-auto">
        <table className="min-w-full text-sm bg-black text-yellow-200 border border-yellow-600 rounded-lg">
          <thead className="bg-yellow-800 text-yellow-100">
            <tr>
              <th className="px-4 py-3 text-left">üë§ Username</th>
              <th className="px-4 py-3 text-left">üîê Role</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-yellow-700">
            {users.map((u, i) => (
              <tr key={i} className="hover:bg-yellow-900/20 transition">
                <td className="px-4 py-2">{u.username}</td>
                <td className="px-4 py-2 font-bold">{u.role}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
