import React, { useState } from 'react';
import toast from 'react-hot-toast';

function Auth({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const login = async () => {
    if (!username || !password) {
      setError("Please enter both fields");
      return;
    }

    try {
      const res = await fetch('/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });

      const data = await res.json();

      if (res.ok) {
        toast.success("Login successful");
        setError('');
        onLogin(data.user); // call the parent with the user info
      } else {
        setError(data.error || "Invalid credentials");
      }
    } catch (err) {
      setError("Error connecting to server");
    }
  };

  return (
    <div className="p-4 bg-gray-100 dark:bg-gray-700 rounded-xl max-w-md mx-auto mt-10">
      <h2 className="text-2xl mb-4 text-black dark:text-white">Login</h2>
      {error && <p className="text-red-500 mb-2">{error}</p>}
      <input
        className="w-full p-2 mb-2 bg-white text-black rounded"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        className="w-full p-2 mb-2 bg-white text-black rounded"
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button
        onClick={login}
        className="w-full bg-blue-600 hover:bg-blue-700 text-white p-2 rounded"
      >
        Login
      </button>
    </div>
  );
}

export default Auth;
