import React, { useEffect, useState } from 'react';

function Dashboard({ user }) {
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState({ username: '', password: '', role: 'user' });

  useEffect(() => {
    fetch('/api/users')
      .then(res => res.json())
      .then(setUsers);
  }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    await fetch('/api/users', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form)
    });
    setForm({ username: '', password: '', role: 'user' });
    // Refresh user list
    const users = await fetch('/api/users').then(res => res.json());
    setUsers(users);
  };

  if (!user || user.role !== "admin") {
    return <div className="text-red-500 p-4">Access denied: Admins only.</div>;
  }

  return (
    <div className="mt-6">
      <h2 className="text-xl mb-2">Admin Dashboard</h2>
      <form onSubmit={handleSubmit} className="mb-4 flex gap-2">
        <input name="username" value={form.username} onChange={handleChange} placeholder="Username" required />
        <input name="password" value={form.password} onChange={handleChange} placeholder="Password" type="password" required />
        <select name="role" value={form.role} onChange={handleChange}>
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit">Add User</button>
      </form>
      <table className="w-full text-sm border">
        <thead>
          <tr>
            <th>Username</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {users.map((u, i) => (
            <tr key={i}>
              <td>{u.username}</td>
              <td>{u.role}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Dashboard;
