import React, { useEffect, useState } from 'react';

function Dashboard({ user }) {
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState({ username: '', password: '', role: 'user' });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const res = await fetch('/api/users');
      const data = await res.json();
      setUsers(data);
    } catch (err) {
      setError('Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const res = await fetch('/api/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
      if (!res.ok) throw new Error('Failed to add user');
      setForm({ username: '', password: '', role: 'user' });
      setMessage('User added successfully');
      fetchUsers();
    } catch (err) {
      setError('Error adding user');
    } finally {
      setTimeout(() => {
        setMessage(null);
        setError(null);
      }, 3000);
    }
  };

  const handleDelete = async username => {
    if (window.confirm(`Delete user ${username}?`)) {
      try {
        await fetch(`/api/users/${username}`, { method: 'DELETE' });
        setMessage(`Deleted ${username}`);
        fetchUsers();
      } catch {
        setError('Error deleting user');
      }
    }
  };

  const handleRoleChange = async (username, newRole) => {
    try {
      await fetch(`/api/users/${username}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ role: newRole })
      });
      setMessage(`Updated role for ${username}`);
      fetchUsers();
    } catch {
      setError('Error updating role');
    }
  };

  if (!user || user.role !== "admin") {
    return <div className="text-red-500 text-center text-lg mt-8 font-semibold">Access Denied: Admins Only</div>;
  }

  return (
    <div className="bg-gray-900 p-6 rounded-xl shadow-xl border border-yellow-500 text-yellow-100 max-w-5xl mx-auto mt-8">
      <h2 className="text-3xl font-extrabold text-yellow-400 mb-6 text-center tracking-wide">üõ†Ô∏è Admin Dashboard</h2>

      <form onSubmit={handleSubmit} className="flex flex-wrap items-center gap-4 mb-6 justify-center">
        <input
          name="username"
          value={form.username}
          onChange={handleChange}
          placeholder="Username"
          required
          className="px-4 py-2 rounded-lg bg-gray-800 text-white border border-yellow-500 focus:outline-none focus:ring-2 focus:ring-yellow-300"
        />
        <input
          name="password"
          value={form.password}
          onChange={handleChange}
          placeholder="Password"
          type="password"
          required
          className="px-4 py-2 rounded-lg bg-gray-800 text-white border border-yellow-500 focus:outline-none focus:ring-2 focus:ring-yellow-300"
        />
        <select
          name="role"
          value={form.role}
          onChange={handleChange}
          className="px-4 py-2 rounded-lg bg-gray-800 text-white border border-yellow-500 focus:outline-none"
        >
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit" className="px-6 py-2 bg-yellow-500 hover:bg-yellow-400 text-black font-bold rounded-lg transition">
          ‚ûï Add User
        </button>
      </form>

      {message && <div className="text-green-400 text-center mb-4 font-semibold">{message}</div>}
      {error && <div className="text-red-500 text-center mb-4 font-semibold">{error}</div>}

      {loading ? (
        <p className="text-yellow-300 text-center text-lg animate-pulse">Loading users...</p>
      ) : users.length === 0 ? (
        <p className="text-yellow-400 text-center">No users found. Add some to get started.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full text-sm bg-black text-yellow-200 border border-yellow-600 rounded-lg">
            <thead className="bg-yellow-800 text-yellow-100">
              <tr>
                <th className="px-4 py-3 text-left">üë§ Username</th>
                <th className="px-4 py-3 text-left">üîê Role</th>
                <th className="px-4 py-3 text-left">‚úèÔ∏è Edit</th>
                <th className="px-4 py-3 text-left">üóëÔ∏è Delete</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-yellow-700">
              {users.map((u, i) => (
                <tr key={i} className="hover:bg-yellow-900/20 transition">
                  <td className="px-4 py-2">{u.username}</td>
                  <td className="px-4 py-2 font-bold">{u.role}</td>
                  <td className="px-4 py-2">
                    <select
                      value={u.role}
                      onChange={e => handleRoleChange(u.username, e.target.value)}
                      className="bg-gray-800 text-white px-2 py-1 rounded border border-yellow-500"
                    >
                      <option value="user">User</option>
                      <option value="admin">Admin</option>
                    </select>
                  </td>
                  <td className="px-4 py-2">
                    {u.username !== user.username && (
                      <button
                        onClick={() => handleDelete(u.username)}
                        className="bg-red-600 hover:bg-red-500 text-white px-3 py-1 rounded"
                      >
                        Delete
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
