import React, { useEffect, useState } from "react";
import { ArrowTrendingUp, RefreshCw } from "lucide-react";

const STRATEGY_LABELS = {
  cross: "Cross-Exchange",
  inter: "Inter-Asset",
  triangular: "Triangular",
  stat: "Statistical",
  funding: "Funding",
};

const STRATEGY_KEYS = Object.keys(STRATEGY_LABELS);

export default function Scanner() {
  const [allOpps, setAllOpps] = useState({});
  const [lastNonEmptyOpps, setLastNonEmptyOpps] = useState({});
  const [strategy, setStrategy] = useState("cross");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [lastSeen, setLastSeen] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch("http://localhost:3000/api/opportunities");
        if (!response.ok) throw new Error("HTTP error! status: " + response.status);
        const data = await response.json();
        setAllOpps(data);
        setLastUpdated(new Date());
        STRATEGY_KEYS.forEach((key) => {
          if (Array.isArray(data[key]) && data[key].length > 0) {
            setLastNonEmptyOpps((prev) => ({ ...prev, [key]: data[key] }));
            setLastSeen((prev) => ({ ...prev, [key]: new Date() }));
          }
        });
      } catch (err) {
        setError("Failed to fetch data. Please ensure the backend server is running.");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
    const intervalId = setInterval(fetchData, 15000);
    return () => clearInterval(intervalId);
  }, []);

  const opps = allOpps[strategy]?.length > 0 ? allOpps[strategy] : lastNonEmptyOpps[strategy] || [];

  return (
    <div className="p-6 bg-[#0f0f0f] border border-yellow-800 rounded-2xl shadow-xl text-yellow-300 animate-fade-in">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-3xl font-bold tracking-wide flex items-center gap-2">
          <ArrowTrendingUp className="w-7 h-7 text-yellow-500" /> Profit Opportunities
        </h2>
        <div className="text-sm text-yellow-400">
          {lastUpdated && (
            <span>Last updated: {lastUpdated.toLocaleTimeString()}</span>
          )}
        </div>
      </div>

      <div className="flex gap-2 mb-6">
        {STRATEGY_KEYS.map((key) => (
          <button
            key={key}
            onClick={() => setStrategy(key)}
            className={`px-4 py-2 rounded-full border border-yellow-700 font-medium text-sm shadow hover:scale-105 transition-all duration-300 ${
              strategy === key
                ? "bg-yellow-500 text-black"
                : "bg-black text-yellow-300 hover:bg-yellow-600 hover:text-black"
            }`}
          >
            {STRATEGY_LABELS[key]}
          </button>
        ))}
      </div>

      <div className="flex justify-between text-sm text-yellow-200 mb-2">
        <span>
          {opps.length > 0 ? (
            <span className="text-green-400 font-semibold">
              {opps.length} live opportunity{opps.length > 1 ? "ies" : ""} found
            </span>
          ) : lastNonEmptyOpps[strategy] ? (
            <span className="text-yellow-500">No current results. Showing last.</span>
          ) : (
            "No opportunities available"
          )}
        </span>
        {lastSeen[strategy] && (
          <span className="italic text-yellow-500">
            Last seen: {lastSeen[strategy].toLocaleTimeString()}
          </span>
        )}
      </div>

      <div className="overflow-x-auto rounded-xl border border-yellow-700 bg-black">
        <table className="min-w-full text-sm">
          <thead className="bg-yellow-800/10 text-yellow-400 text-left">
            <tr>
              {strategy === "cross" && (
                <>
                  <th className="px-4 py-3">Pair</th>
                  <th className="px-4 py-3">Buy Exchange</th>
                  <th className="px-4 py-3">Buy Price</th>
                  <th className="px-4 py-3">Sell Exchange</th>
                  <th className="px-4 py-3">Sell Price</th>
                  <th className="px-4 py-3">Profit %</th>
                </>
              )}
            </tr>
          </thead>
          <tbody className="divide-y divide-yellow-900">
            {opps.map((op, i) => (
              <tr
                key={i}
                className={`${
                  parseFloat(op.profitPercent || op.profitPercentage) > 0.2
                    ? "bg-yellow-900/20 font-semibold animate-pulse"
                    : "hover:bg-yellow-900/10"
                } transition-all duration-300`}
              >
                {strategy === "cross" && (
                  <>
                    <td className="px-4 py-3">{op.pair}</td>
                    <td className="px-4 py-3">{op.buyEx || op.buyExchange}</td>
                    <td className="px-4 py-3 text-green-400 font-medium">
                      ${parseFloat(op.buyPrice).toLocaleString()}
                    </td>
                    <td className="px-4 py-3">{op.sellEx || op.sellExchange}</td>
                    <td className="px-4 py-3 text-green-400 font-medium">
                      ${parseFloat(op.sellPrice).toLocaleString()}
                    </td>
                    <td className="px-4 py-3 text-yellow-400 font-bold">
                      {(op.profitPercent || op.profitPercentage)}%
                    </td>
                  </>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {loading && (
        <p className="text-yellow-400 mt-4 flex items-center gap-2 animate-pulse">
          <RefreshCw className="animate-spin w-4 h-4" /> Fetching latest data...
        </p>
      )}

      {error && (
        <p className="text-red-500 mt-4 font-semibold text-center">{error}</p>
      )}
    </div>
  );
}
