import React, { useState, useEffect } from 'react';
// No need to import EXCHANGES here if you're fetching from your backend
import { exportToCSV } from './utils/exportCSV';

function Scanner() {
  const [opps, setOpps] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // This now calls your backend server
        // Ensure your backend server (node server.js) is running
        // You can add more pairs here if you want your backend to check more
        const response = await fetch('/api/arbitrage?pairs=BTC-USDT,ETH-USDT,XRP-USDT,LTC-USDT,ADA-USDT');

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const opportunities = await response.json(); // The backend sends the processed opportunities

        // Filter opportunities to only include those over 5% profit
        const filteredOpps = opportunities.filter(op => parseFloat(op.profitPercentage) > 5);

        setOpps(filteredOpps);

      } catch (err) {
        console.error('Error fetching arbitrage opportunities from backend:', err);
        setOpps([]); // Clear opportunities on error
      } finally {
        setLoading(false);
      }
    };

    // Fetch data immediately when the component mounts
    fetchData();

    // Optionally, refresh data periodically (e.g., every 30 seconds)
    // const intervalId = setInterval(fetchData, 30000);
    // return () => clearInterval(intervalId); // Cleanup on unmount

  }, []); // Empty dependency array means this effect runs once on mount

  return (
    <div className="mb-4">
      <div className="flex justify-between items-center mb-2">
        <h2 className="text-xl font-semibold">Opportunities over 5%</h2>
        <button onClick={() => exportToCSV(opps)} className="bg-blue-600 px-4 py-1 rounded text-white">Export CSV</button>
      </div>
      {loading ? <p>Loading...</p> : (
        <table className="w-full border">
          <thead>
            <tr>
              <th>Pair</th>
              <th>Buy From</th>
              <th>Sell To</th>
              <th>Profit %</th>
            </tr>
          </thead>
          <tbody>
            {opps.length > 0 ? (
              opps.map((op, i) => (
                <tr key={i}>
                  <td>{op.pair}</td>
                  <td>{op.buyExchange}</td>
                  <td>{op.sellExchange}</td>
                  <td className="text-green-600 font-bold">{op.profitPercentage}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4" className="text-center py-4">No opportunities over 5% found.</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default Scanner;
